import React from 'react'
import signinStyles from "../styles/Signin.module.css";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faXmark, faEyeSlash, faEye  } from "@fortawesome/free-solid-svg-icons";
import { useState, useEffect } from "react";
import styles from "../styles/signup.module.css";
import {toast} from 'react-toastify'

const Password = (props) => {
  const [isSubmit, setIsSubmit]= useState(false);
  const [isPasswordValid, setIsPasswordValid]= useState(false);
  const [isPasswordBlank, setIsPasswordBlank]= useState(true);
  const [showPassword, setShowPassword] = useState(false);
  var toastInfo= {
    position: "top-center",
    autoClose: 5000,
    hideProgressBar: false,
    closeOnClick: true,
    pauseOnHover: true,
    draggable: true,
    progress: undefined,
    theme: "dark",
    };

  


  const handleSubmit=async(e)=>{
    e.preventDefault();
    
    const url= 'http://localhost:8000/verifyOtp';
    const dataToSubmit={
    }
    const response= await fetch(url, {
      method: 'POST',
      headers: {
        "Content-type": "application/json",
      },
      body: JSON.stringify(dataToSubmit)
    })
    
    if(response.status== 200){
      toast.success('Email is verified successfully!', toastInfo)
      props.onNextClick();
    }
    if(response.status== 401){
      toast.warning('OTP has been expired! Please click on Resend code', toastInfo)
    }
    if(response.status== 404){
      toast.error('Incorrect OTP!', toastInfo);
      e.target[0].value="";
    }
  }
  
  return (
    <>
    <div className={styles.signUpContainer}></div>
      <div className={styles.signUpBox}>
        <button
          className={styles.signupCloseButton}
          onClick={props.onClose}
        >
          <FontAwesomeIcon icon={faXmark} size="lg" />
        </button>
        <div className={styles.signUpInfo} >
          <h3
            style={{ fontSize: "20px", fontWeight: "600", lineHeight: "25px" }}
          >
            Sign up
          </h3>

          <form action="" method="" style={{width: '100%'}} onSubmit={handleSubmit}>
            <div className={signinStyles.formInputDiv} style={{width: '90%', height:"100px"}}>
              <label>
                <p className={signinStyles.formLabel} style={{fontSize: 'small', fontWeight:'bold'}}>Password</p>
              </label>
              <div style={{width: '100%', marginTop:'10px', display:'flex', justifyContent:'space-between', alignItems:'center'}}>
                <input
                    type=""
                    className={(!isPasswordValid && !isPasswordBlank)? styles.invalidInputError :signinStyles.formInput}
                    style={{width: '90%'}}
                ></input>
                {!showPassword && <FontAwesomeIcon icon={faEyeSlash} color='grey' style={{cursor: 'pointer'}} onClick={()=>setShowPassword(true)}/>}
                {showPassword && <FontAwesomeIcon icon={faEye} color='grey' style={{cursor: 'pointer'}} onClick={()=>setShowPassword(false)}/>}
              </div>
              
              {!isPasswordBlank? <div className={styles.invalidInputBox}>
             Message
              </div> :null}
            </div>

            <div style={{ width: '100%', borderBottom: '1px solid lightGray', marginTop: '120px'}}>
            </div>
            <button type="submit" className={signinStyles.formLoginButton} disabled={!isSubmit}
            style={{width: '15%', marginTop: '10px', position: 'relative', left: '40%'}}
            >
              Finish
            </button>
          </form>
        </div>
      </div>
    </>
  )
}

export default Password
