import styles from "../styles/signup.module.css";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faXmark } from "@fortawesome/free-solid-svg-icons";
import signinStyles from "../styles/Signin.module.css";
import { useState, useEffect } from "react";
function SignUp(props) {

  const [goNext, setGoNext]= useState(false);
  const [name, setName]= useState('');
  const [email, setEmail]= useState('');
  const [isEmailValid, setIsEmailValid]= useState(true);

  const validateEmail = (email) => {
    return String(email)
      .toLowerCase()
      .match(
        /^(([^<>()[\]\\.,;:\s@"]+(\.[^<>()[\]\\.,;:\s@"]+)*)|(".+"))@((\[[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}\])|(([a-zA-Z\-0-9]+\.)+[a-zA-Z]{2,}))$/
      );
  };
  function handleBtn(email){
    validateEmail(email)
  }

  useEffect(() => {
    return () => {
      if(validateEmail){
        setEmail(email);
        setGoNext(true);
      }


    }
  }, [])
  

  return (
    <>
      <div className={styles.signUpContainer}></div>
      <div className={styles.signUpBox}>
        <button
          className={styles.signupCloseButton}
          onClick={props.onSignUpClose}
        >
          <FontAwesomeIcon icon={faXmark} size="lg" />
        </button>
        <div className={styles.signUpInfo} >
          <h3
            style={{ fontSize: "18px", fontWeight: "1000", lineHeight: "25px" }}
          >
            Sign up
          </h3>

          <form action="" method="" style={{width: '100%'}}>
            <div className={signinStyles.formInputDiv} style={{width: '90%'}}>
              <label>
                <h5 className={signinStyles.formLabel}>Name</h5>
              </label>
              <input
                type="text"
                placeholder="What would you like to be called?"
                className={signinStyles.formInput}
                style={{width: '100%'}}
                onChange={(e)=> setName(e.target.value)}
              ></input>
            </div>

            <div className={signinStyles.formInputDiv} style={{width: '90%'}}>
              <label>
                <h5 className={signinStyles.formLabel}>Email</h5>
              </label>
              <input
                type="email"
                placeholder="Your email"
                className={signinStyles.formInput}
                style={{width: '100%'}}
                onChange={(e)=> handleBtn(e.target.value)}
              ></input>
            </div>

            <div style={{ width: '100%', borderBottom: '1px solid lightGray', marginTop: '35px'}}>

            </div>
            <button className={signinStyles.formLoginButton} disabled={goNext}
            style={{width: '15%', marginTop: '10px', position: 'relative', left: '40%'}}
            >
              Next
            </button>
          </form>
        </div>
      </div>
    </>
  );
}

export default SignUp;
